function setupSeries()
{
	var groupstr = '';
	for (i = 1; i < 119; i++)
	{
		if (elementData[i-1][9] == 0)
		{
			groupstr = "nonmetal";
		}
		else if (elementData[i-1][9] == 1)
		{
			groupstr = "alkali";
		}
		else if (elementData[i-1][9] == 2)
		{
			groupstr = "alkaline";
		}
		else if ((elementData[i-1][9] >= 3) && (elementData[i-1][9] <= 12))
		{
			groupstr = "transition";
		}
		else if (elementData[i-1][9] == 13)
		{
			groupstr = "boron";
		}
		else if (elementData[i-1][9] == 14)
		{
			groupstr = "carbon";
		}
		else if (elementData[i-1][9] == 15)
		{
			groupstr = "pnictogen";
		}
		else if (elementData[i-1][9] == 16)
		{
			groupstr = "chalcogen";
		}
		else if (elementData[i-1][9] == 17)
		{
			groupstr = "halogen";
		}
		else if (elementData[i-1][9] == 18)
		{
			groupstr = "noble";
		}
		else if (elementData[i-1][9] == 101)
		{
			groupstr = "lanthanoid";
		}
		else if (elementData[i-1][9] == 102)
		{
			groupstr = "actinoid";
		}
		setElementClass(document.getElementById("element"+i), groupstr);
		
	}
 
    
}

function displayElementData(element, elementNumber)
{
    var html = '';
    html += '<div class="seriesAtomNum">' + elementData[elementNumber-1][0] + '</div>';
    html += '<div class="seriesSymbol">' + elementData[elementNumber-1][3] + '</div>';
    html += '<div class="seriesAtomWeight">' + elementData[elementNumber-1][1] + '</div>';
    element.innerHTML = html;
}


function displayAllElementData()
{
    for (var i = 1; i < 119; i++)
    {
        displayElementData(document.getElementById("element" + i), i);
    }
}

function getBackgroundColor(elementNumber)
{
	var colorstr = '';
	if (elementData[elementNumber-1][9] == 0)
	{
		colorstr = "#eeeeee";
	}
	else if (elementData[elementNumber-1][9] == 1)
	{
		colorstr = "#ffcc00";
	}
	else if (elementData[elementNumber-1][9] == 2)
	{
		colorstr = "#ffff00";
	}
	else if ((elementData[elementNumber-1][9] >= 3) && (elementData[elementNumber-1][9] <= 12))
	{
		colorstr = "#ff9999";
	}
	else if (elementData[elementNumber-1][9] == 13)
	{
		colorstr = "#999999";
	}
	else if (elementData[elementNumber-1][9] == 14)
	{
		colorstr = "#33cc33";
	}
	else if (elementData[elementNumber-1][9] == 15)
	{
		colorstr = "#33ff00";
	}
	else if (elementData[elementNumber-1][9] == 16)
	{
		colorstr = "#00ffcc";
	}
	else if (elementData[elementNumber-1][9] == 17)
	{
		colorstr = "#009999";
	}
	else if (elementData[elementNumber-1][9] == 18)
	{
		colorstr = "#006699";
	}
	else if (elementData[elementNumber-1][9] == 101)
	{
		colorstr = "#ff6633";
	}
	else if (elementData[elementNumber-1][9] == 102)
	{
		colorstr = "#ffcc66";
	}

	return colorstr;
}

function loadInfoBox(elementNumber)
{
    document.getElementById("infoBox").style.visibility = 'visible';
    document.getElementById("infoBox").style.backgroundColor = getBackgroundColor(elementNumber);
    var html = '';
    html += '<table border="0" cellspacing="0" cellpadding="0">';
    html += '<tr><td class="infoBoxtrbold">Symbol:&nbsp;</td><td class="infoBoxtr">' + elementData[elementNumber-1][3] + '</td>';
    if (elementData[elementNumber-1][4] == '')
    {
        html += '<td class="infoBoxtrbold">&nbsp;Melting Point:&nbsp;</td><td class="infoBoxtr">Unknown</td></tr>';
    }
    else
    {
        html += '<td class="infoBoxtrbold">&nbsp;Melting Point:&nbsp;</td><td class="infoBoxtr">' + elementData[elementNumber-1][4] + 'C</td></tr>';
    }
    html += '<tr><td class="infoBoxtrbold">Name:&nbsp;</td><td class="infoBoxtr">' + elementData[elementNumber-1][2] + '</td>';
    if (elementData[elementNumber-1][5] == '')
    {
        html += '<td class="infoBoxtrbold">&nbsp;Boiling Point:&nbsp;</td><td class="infoBoxtr">Unknown</td></tr>';
    }
    else
    {
		html += '<td class="infoBoxtrbold">&nbsp;Boiling Point:&nbsp;</td><td class="infoBoxtr">' + elementData[elementNumber-1][5] + 'K</td></tr>';
    }
    html += '<tr><td class="infoBoxtrbold">Atm Number:&nbsp;</td><td class="infoBoxtr">' + elementData[elementNumber-1][0] + '</td>';
    html += '<td class="infoBoxtrbold">Discovered:&nbsp;</td><td class="infoBoxtr">' + elementData[elementNumber-1][8] + '</td></tr>';
    html += '<tr><td class="infoBoxtrbold">Atm Weight:&nbsp;</td><td class="infoBoxtr">' + elementData[elementNumber-1][1] + '</td>';
    if (elementData[elementNumber-1][6] == '')
    {
		html += '<td class="infoBoxtrbold">&nbsp;Density:&nbsp;</td><td class="infoBoxtr">Unknown</td></tr>';
    }
    else
    {
		if (elementData[elementNumber-1][5] > 100)
		{
			html += '<td class="infoBoxtrbold">&nbsp;Density:&nbsp;</td><td class="infoBoxtr">' + elementData[elementNumber-1][6] + 'g/l</td></tr>';
		}
		else
		{
			html += '<td class="infoBoxtrbold">&nbsp;Density:&nbsp;</td><td class="infoBoxtr">' + elementData[elementNumber-1][6] + 'g/cm^3</td></tr>';
		}
		
    }
    if (elementData[elementNumber-1][10] == '')
    {
		html += '<tr><td class="infoBoxtrbold">Electron Config:&nbsp;</td><td class="infoBoxtr">Unknown</td>';
    }
    else
    {
		html += '<tr><td class="infoBoxtrbold">Electron Config:&nbsp;</td><td class="infoBoxtr">' + elementData[elementNumber-1][10] + '</td>';
    }
    if (elementData[elementNumber-1][11] == '')
    {
		html += '<td class="infoBoxtrbold">&nbsp;Ioniz. Energy:&nbsp;</td><td class="infoBoxtr">Unknown</td></tr>';
    }
    else
    {
		html += '<td class="infoBoxtrbold">&nbsp;Ioniz. Energy:&nbsp;</td><td class="infoBoxtr">' + elementData[elementNumber-1][11] + 'kJ/mol</td></tr>';
    }
    html += '</table>';
    document.getElementById("infoBox").innerHTML = html;
}

function hideInfoBox()
{
    document.getElementById("infoBox").style.visibility = 'hidden';
}






